import { Grid, Typography } from '@mui/material';
import React from 'react';
import CourseCard from './CourseCard';
import LatestNewsCard from './LatestNewsCard';
import TopLearnerCard from './TopLearnerCard';
import { FaArrowRight, FaArrowLeft } from "react-icons/fa";
import Slider from "react-slick";

const Home = () => {
  let slides = [
    <img  src="https://picsum.photos/800/300/?random" alt="1" />,
    <img  src="https://picsum.photos/800/301/?random" alt="2" />  ,
    <img  src="https://picsum.photos/800/302/?random" alt="3" />  , 
    <img  src="https://picsum.photos/800/303/?random" alt="4" />  ,
    <img src="https://picsum.photos/800/304/?random" alt="5" />  
  ];

  return ( 
    <Grid container spacing={4}>
      <Grid item xs={12}>
        <Slider>
          {slides.map((img, idx) => (
            <div>   
              <img src={img} alt={idx} />
            </div>
          ))}
        </Slider>
      </Grid>

      <Grid item xs={5}>
        <Typography className='inviationHeaderTitle' variant='h6'>TRENDING COURSES</Typography>

        {[1, 2, 3].map((card) => (
          <CourseCard />
        ))};
      </Grid>

      <Grid item xs={3}>
        <Typography className='inviationHeaderTitle' variant='h6'>TOP LEARNERS</Typography>

        {[1, 2, 3, 4, 5].map((card) => (
          <TopLearnerCard  />
        ))};
      </Grid>

      <Grid item xs={4}>
        <Typography className='inviationHeaderTitle' variant='h6'>LATEST NEWS</Typography>

          {[1, 2, 3, 4, 5].map((card) => (
            <LatestNewsCard  />
          ))};
        </Grid>
    </Grid>
   );
}

export default Home;
