import { Button, Grid, TextField, Typography } from '@mui/material';
import React from 'react';

const CreateCollection = () => {

  const [step, setStep] = React.useState(1); 

  const next = () => {
    if (step !== 3) {
        setStep(step+1);
    }
  }

  const back = () => {
    if (step !== 1) {
        setStep(step-1);
    }
  }

  return ( 
    <Grid container>
      <Grid item xs={12}>
        <Typography className='inviationHeaderTitle' variant='h6'>New Collection - Step 01</Typography>
      </Grid>


      {step === 1 && (
        <Grid item xs={12}>
            <Grid container className='createCollectionBox'>
                <Grid xs={2}>
                    <Typography className='collectionFieldTitle'>Title</Typography>
                </Grid>
                <Grid xs={10}>
                    <TextField
                        variant="standard"           
                        InputProps={{
                            disableUnderline: true,
                            style: { padding: '8px 20px' },
                        }} className='collectionField' fullWidth placeholder="Collection title" />
                </Grid>

                <Grid xs={2}>
                    <Typography className='collectionFieldTitle'>Description</Typography>
                </Grid>
                <Grid xs={10}>
                    <TextField
                        variant="standard"           
                        InputProps={{
                            disableUnderline: true,
                            style: { padding: '8px 20px' },
                        }} className='collectionField collectionDesc' fullWidth placeholder="Collection description" />
                </Grid>

                <Grid xs={2}>
                    <Typography className='collectionFieldTitle'>Source</Typography>
                </Grid>
                <Grid xs={10}>
                    <TextField
                        variant="standard"           
                        InputProps={{
                            disableUnderline: true,
                            style: { padding: '8px 20px' },
                        }} className='collectionField' fullWidth placeholder="Collection source (Google drive, Youtube, etc)" />
                </Grid>

                <Grid xs={2}>
                    <Typography className='collectionFieldTitle'>Tag</Typography>
                </Grid>
                <Grid xs={10}>
                    <TextField
                        variant="standard"           
                        InputProps={{
                            disableUnderline: true,
                            style: { padding: '8px 20px' },
                        }} className='collectionField' fullWidth placeholder="Collection tag (tag input)" />
                </Grid>

            </Grid>
            </Grid>
      )}



      <Grid item xs={12} className='collectionButtons'>
        <Button className='backCollectionButton'>Back</Button>
        <Button className='nextCollectionButton'>Next</Button>
      </Grid>
    </Grid>
  );
}

export default CreateCollection;
