import { Grid, Typography } from '@mui/material';
import React from 'react';
import CourseCard from './CourseCard';
import LatestNewsCard from './LatestNewsCard';
import TopLearnerCard from './TopLearnerCard';
import RightIcon from '../../assets/right.png';
import LeftIcon from '../../assets/left.png';

import SliderImage1 from '../../assets/slider/slider-01.jpg';
import SliderImage2 from '../../assets/slider/slider-02.jpg';
import SliderImage3 from '../../assets/slider/slider-03.jpg';
import SliderImage4 from '../../assets/slider/slider-04.jpg';
import SliderImage5 from '../../assets/slider/slider-05.jpg';

import M from "materialize-css";
import "materialize-css/dist/css/materialize.min.css";
import { Carousel } from 'react-materialize';


const Home = () => {
  let slides = [
    <img width={500} src={SliderImage1} alt="1" />,
    <img width={500} src={SliderImage2} alt="2" />  ,
    <img width={500} src={SliderImage3} alt="3" />  , 
    <img width={500} src={SliderImage4} alt="4" />  ,
    <img width={500} src={SliderImage5} alt="5" />  
  ];

  React.useEffect(() => {
    const options = {
      duration: 300,
      onCycleTo: () => {
        console.log("New Slide");
      }
    };
    M.Carousel.init(this.Carousel, options);
  }, []);

  return ( 
    <Grid container spacing={4}>
      <Grid item xs={12} style={{ height: '400px', margin: '50px 0px'}}>
        <Carousel
          carouselId="Carousel-33"
          images={[
            SliderImage1,
            SliderImage2,
            SliderImage3,
            SliderImage4,
            SliderImage4
          ]}
          options={{
            dist: -100,
            duration: 200,
            fullWidth: false,
            indicators: false,
            noWrap: false,
            numVisible: 5,
            onCycleTo: null,
            padding: 0,
            shift: 0
          }}
        />
      </Grid>

      <Grid item xs={5}>
        <Typography className='inviationHeaderTitle homepageTitle' variant='h6'>Trending Collections</Typography>

        {[1, 2, 3].map((card) => (
          <CourseCard />
        ))}
      </Grid>

      <Grid item xs={3}>
        <Typography className='inviationHeaderTitle homepageTitle' variant='h6'>Top Learner</Typography>

        {[1, 2, 3, 4, 5].map((card) => (
          <TopLearnerCard  />
        ))}
      </Grid>

      <Grid item xs={4}>
        <Typography className='inviationHeaderTitle homepageTitle' variant='h6'>Latest News</Typography>

          {[1, 2, 3, 4, 5].map((card) => (
            <LatestNewsCard  />
          ))}
        </Grid>
    </Grid>
   );
}

export default Home;
